mapper.select:
select p.project_id, p.dept_id, p.project_name, p.principal_id, p.expenses_total, p.expenses_left, p.status,
d.dept_name, u.nick_name,f.proc_def_id,f.deploy_id from project p
left join sys_dept d on p.dept_id = d.dept_id
left join sys_user u on p.principal_id = u.user_id
left join project_flow f on p.project_id = f.project_id

    and dept_id = #{deptId}
    and p.project_id = #{projectId}
    and project_name like concat('%', #{projectName},
        '%')

    and principal_id = #{principalId}
    and expenses_total = #{expensesTotal}
    and expenses_left = #{expensesLeft}

mapper.update:
update project
dept_id = #{deptId}, project_name = #{projectName}, principal_id = #{principalId}, expenses_total = #{expensesTotal}, expenses_left = #{expensesLeft}, status = #{status},
where project_id = #{projectId}

mapper.select:
select p.project_id, p.dept_id, p.project_name, p.principal_id, p.status, d.dept_name, u.nick_name from project p left join sys_dept d on p.dept_id = d.dept_id left join sys_user u on p.principal_id = u.user_id p.status = '0'
AND project_id = #{projectId}
ANd project_name like concat('%', #{projectName}, '%')
